      out.write("					} else {\r\n");
      out.write("						// Remove the flag (AND the inverse of the flag)\r\n");
      out.write("						viewModels.loggedInUserModel().user.successMessageFlags(viewModels.loggedInUserModel().user.successMessageFlags() & ~successMessageType);\r\n");
      out.write("					}\r\n");
      out.write("\r\n");
      out.write("					bungieNetPlatform.userService.EditSuccessMessageFlags(\r\n");
      out.write("						viewModels.loggedInUserModel().user.successMessageFlags(),\r\n");
